@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix soma: <http://www.ease-crc.org/ont/SOMA.owl#> .
@prefix dul: <http://www.ontologydesignpatterns.org/ont/dul/DUL.owl#> .
@prefix geo: <http://www.opengis.net/ont/geosparql#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix obot: <https://w3id.org/onto-bot#> .

### Knowledge Graph of Actions

## Classes and Object Properties

obot:Instruction a owl:Class, dul:Entity ;
 rdfs:label "Instruction"@en ;
 rdfs:comment "An instruction is a command or directive given to a robot."@en .

obot:Workflow a owl:Class, dul:Entity ;
 rdfs:subClassOf dul:Workflow ;
 rdfs:label "Workflow"@en ;
 rdfs:comment "A workflow is a sequence of tasks or processes that are carried out to fulfil the instruction."@en .

dul:Action a owl:Class, dul:Entity ;
 rdfs:label "Action"@en ;
 rdfs:comment "An action is an atomic, single, clear action that is carried out to fulfil the instruction."@en .

dul:Agent a owl:Class, dul:Entity ;
 rdfs:label "Agent"@en ;
 rdfs:comment "An agent performing an action."@en .

obot:Component a owl:Class, dul:Entity ;
 rdfs:label "Component"@en ;
 rdfs:comment "A component is a part of an environment that can be used to perform tasks or actions."@en .

obot:Appliance a obot:Component ;
 rdfs:label "Appliance"@en ;
 rdfs:comment "An appliance is a type of component."@en .

obot:Furniture a obot:Component ;
 rdfs:label "Furniture"@en ;
 rdfs:comment "A furniture is a type of component."@en .

obot:Object a obot:Component ;
 rdfs:label "Object"@en ;
 rdfs:comment "An object is a type of component."@en .

obot:Affordance a owl:Class, dul:Entity ;
 rdfs:label "Affordance"@en ;
 rdfs:comment "An affordance is a property of an object that indicates how it can be used."@en .

soma:Closing a obot:Affordance ;
 rdfs:label "Closing"@en ;
 rdfs:comment "Closing is an affordance that enables a component to be closed."@en .

soma:Opening a obot:Affordance ;
 rdfs:label "Opening"@en ;
 rdfs:comment "Opening is an affordance that enables a component to be opened."@en .

obot:hasWorkflow a owl:ObjectProperty ;
 rdfs:domain obot:Instruction ;
 rdfs:range obot:Workflow ;
 rdfs:label "has workflow"@en ;
 rdfs:comment "An instruction has a workflow that needs to be executed."@en .

obot:hasAction a owl:ObjectProperty ;
 rdfs:domain obot:Workflow ;
 rdfs:range dul:Action ;
 rdfs:label "has action"@en ;
 rdfs:comment "A workflow has one or multiple clear atomic actions."@en .

obot:precedes a owl:ObjectProperty ;
 rdfs:subPropertyOf dul:precedes ;
 rdfs:domain dul:Action ;
 rdfs:range dul:Action ;
 rdfs:label "precedes" ;
 rdfs:comment "An action can precede another action."@en .

obot:follows a owl:ObjectProperty ;
 rdfs:subPropertyOf dul:follows ;
 rdfs:domain dul:Action ;
 rdfs:range dul:Action ;
 rdfs:label "follow" ;
 rdfs:comment "An action can follow another action."@en .

soma:isPerformedBy a owl:ObjectProperty ;
 rdfs:domain dul:Action ;
 rdfs:range dul:Agent ;
 rdfs:label "is performed by"@en ;
 rdfs:comment "An action is performed by an agent."@en .

obot:actsOn a owl:ObjectProperty ;
 rdfs:domain dul:Action ;
 rdfs:range obot:Component ;
 rdfs:label "acts on"@en ;
 rdfs:comment "An action acts on a component."@en .

obot:isAffordedBy a owl:ObjectProperty ;
 rdfs:domain dul:Action ;
 rdfs:range obot:Affordance ;
 rdfs:label "is afforded by"@en ;
 rdfs:comment "An action is afforded by an affordance."@en .

## Data Properties

obot:hasNaturalLanguage a owl:DatatypeProperty ;
 rdfs:domain obot:Instruction ;
 rdfs:range xsd:string ;
 rdfs:label "has natural language"@en ;
 rdfs:comment "The instruction is expressed in a natural language."@en .

## Individuals and Actions

### Instruction
obot:RestoreKitchen a obot:Instruction ;
 rdfs:label "Restore Kitchen"@en ;
 obot:hasNaturalLanguage "Restore the kitchen to an organized state by identifying all misplaced items and returning them to their standard storage locations."@en .

### Workflow
obot:OrganizeKitchen a obot:Workflow ;
 rdfs:label "Organize Kitchen Workflow"@en .

### Actions
obot:PickUpWaterBottle a dul:Action ;
 rdfs:label "Pick Up Water Bottle"@en ;
 obot:actsOn <https://example.org/Object/WaterBottle> ;
 obot:isAffordedBy soma:PickingUp .

obot:PickUpMarmite a dul:Action ;
 rdfs:label "Pick Up Marmite"@en ;
 obot:actsOn <https://example.org/Object/Marmite> ;
 obot:isAffordedBy soma:PickingUp .

obot:PickUpJello a dul:Action ;
 rdfs:label "Pick Up Jello"@en ;
 obot:actsOn <https://example.org/Object/Jello> ;
 obot:isAffordedBy soma:PickingUp .

obot:PickUpAlpenSalt a dul:Action ;
 rdfs:label "Pick Up Alpen Salt"@en ;
 obot:actsOn <https://example.org/Object/AlpenSalt> ;
 obot:isAffordedBy soma:PickingUp .

obot:PickUpRiceBubbles a dul:Action ;
 rdfs:label "Pick Up Rice Bubbles"@en ;
 obot:actsOn <https://example.org/Object/RiceBubbles> ;
 obot:isAffordedBy soma:PickingUp .

obot:PickUpShortbread a dul:Action ;
 rdfs:label "Pick Up Shortbread"@en ;
 obot:actsOn <https://example.org/Object/Shortbread> ;
 obot:isAffordedBy soma:PickingUp .

obot:PickUpChucky a dul:Action ;
 rdfs:label "Pick Up Chucky"@en ;
 obot:actsOn <https://example.org/Object/Chucky> ;
 obot:isAffordedBy soma:PickingUp .

obot:PutDownWaterBottleFridge a dul:Action ;
 rdfs:label "Put Down Water Bottle in Fridge"@en ;
 obot:actsOn <https://example.org/Object/WaterBottle> ;
 obot:isAffordedBy soma:PuttingDown .

obot:PutDownMarmiteCabinet a dul:Action ;
 rdfs:label "Put Down Marmite in Cabinet"@en ;
 obot:actsOn <https://example.org/Object/Marmite> ;
 obot:isAffordedBy soma:PuttingDown .

obot:PutDownJelloCabinet a dul:Action ;
 rdfs:label "Put Down Jello in Cabinet"@en ;
 obot:actsOn <https://example.org/Object/Jello> ;
 obot:isAffordedBy soma:PuttingDown .

obot:PutDownAlpenSaltCabinet a dul:Action ;
 rdfs:label "Put Down Alpen Salt in Cabinet"@en ;
 obot:actsOn <https://example.org/Object/AlpenSalt> ;
 obot:isAffordedBy soma:PuttingDown .

obot:PutDownRiceBubblesCabinet a dul:Action ;
 rdfs:label "Put Down Rice Bubbles in Cabinet"@en ;
 obot:actsOn <https://example.org/Object/RiceBubbles> ;
 obot:isAffordedBy soma:PuttingDown .

obot:PutDownShortbreadCabinet a dul:Action ;
 rdfs:label "Put Down Shortbread in Cabinet"@en ;
 obot:actsOn <https://example.org/Object/Shortbread> ;
 obot:isAffordedBy soma:PuttingDown .

obot:PutDownChuckyCabinet a dul:Action ;
 rdfs:label "Put Down Chucky in Cabinet"@en ;
 obot:actsOn <https://example.org/Object/Chucky> ;
 obot:isAffordedBy soma:PuttingDown .

## Workflow and Action Sequence

obot:RestoreKitchen obot:hasWorkflow obot:OrganizeKitchen .

obot:OrganizeKitchen obot:hasAction obot:PickUpWaterBottle .
obot:OrganizeKitchen obot:hasAction obot:PickUpMarmite .
obot:OrganizeKitchen obot:hasAction obot:PickUpJello .
obot:OrganizeKitchen obot:hasAction obot:PickUpAlpenSalt .
obot:OrganizeKitchen obot:hasAction obot:PickUpRiceBubbles .
obot:OrganizeKitchen obot:hasAction obot:PickUpShortbread .
obot:OrganizeKitchen obot:hasAction obot:PickUpChucky .

obot:PickUpWaterBottle obot:precedes obot:PutDownWaterBottleFridge .
obot:PickUpMarmite obot:precedes obot:PutDownMarmiteCabinet .
obot:PickUpJello obot:precedes obot:PutDownJelloCabinet .
obot:PickUpAlpenSalt obot:precedes obot:PutDownAlpenSaltCabinet .
obot:PickUpRiceBubbles obot:precedes obot:PutDownRiceBubblesCabinet .
obot:PickUpShortbread obot:precedes obot:PutDownShortbreadCabinet .
obot:PickUpChucky obot:precedes obot:PutDownChuckyCabinet .
